      Email:"",
      Description:"",
   })



   const GetData = (e)=>{
      const Value = e.target.value;
      const Name = e.target.name;
      InsertData((prev)=>{
        if(Name === "Fname"){
          return {
              Fname:Value
          }
        }else if(Name === "Lname"){
         return {
            ...prev,
            Lname:Value
        }
       }
       else if(Name === "Number"){
         return {
            ...prev,
            Number:Value
        }
       }else if(Name ==="Email"){
         return {
            ...prev,
            Email:Value
        }
       }else if(Name === "Description"){
         return {
            ...prev,
            Description:Value
        }
       }
      })
   }

